      // imageData有4个通道rgba
      for (let i = 0; i < imageData.length; i += 4) {
        let sum_rgb = 0
        // 但我们只需要rgb三通道，a-alpha通道无用
        for (let j = 0; j < 3; j += 1) {
          sum_rgb = sum_rgb + imageData[i + j]
        }
        let grey = Math.round(sum_rgb / 3)
